mapper.select:
select f.feedNo, f.feedLimitContent, f.feedGoodCnt, f.feedReplyCnt, f.feedDate, f.feedUpdate, f.feedLock, f.categoryTemp, c.categoryNo, c.bigGroup, c.smallGroup, u.userId, u.userNick, u.userImgUuid, u.userImgUploadPath, u.userImgFileName, g.userId AS isGood, fv.userId AS isFavorite, r.userId AS isReply from feed f, category c, userInfo u, good g, favorite fv, reply r where f.categoryNo = c.categoryNo AND f.userId = u.userId AND (g.userId(+) = f.userId AND g.feedNo(+) = f.FEEDNO) AND (fv.userId(+) = f.userId AND fv.feedNo(+) = f.FEEDNO) AND (r.userId(+) = f.userId AND r.feedNo(+) = f.FEEDNO) AND f.userId = #{filter.goToUserId} AND f.feedDel = 'F' AND rownum <= 10
and f.feedNo < #{filter.page}
order by f.feedno desc

select f.feedNo, f.feedLimitContent, f.feedGoodCnt, f.feedReplyCnt, f.feedDate, f.feedUpdate, f.feedLock, f.categoryTemp, c.categoryNo, c.bigGroup, c.smallGroup, u.userId, u.userNick, u.userImgUuid, u.userImgUploadPath, u.userImgFileName, g.userId AS isGood, fv.userId AS isFavorite, r.userId AS isReply from feed f, category c, userInfo u, good g, favorite fv, reply r where f.categoryNo = c.categoryNo AND f.userId = u.userId AND (g.userId(+) = #{user.userId} AND g.feedNo(+) = f.FEEDNO) AND (fv.userId(+) = #{user.userId} AND fv.feedNo(+) = f.FEEDNO) AND (r.userId(+) = #{user.userId} AND r.feedNo(+) = f.FEEDNO) AND fv.userId = #{user.userId} AND fv.FEEDNO = f.FEEDNO AND f.feedDel = 'F' AND rownum <= 10
and f.feedNo < #{filter.page}
order by f.feedno desc

select f.feedNo, f.feedLimitContent, f.feedGoodCnt, f.feedReplyCnt, f.feedDate, f.feedUpdate, f.feedLock, f.categoryTemp, c.categoryNo, c.bigGroup, c.smallGroup, u.userId, u.userNick, u.userImgUuid, u.userImgUploadPath, u.userImgFileName, g.userId AS isGood, fv.userId AS isFavorite, r.userId AS isReply from feed f, category c, userInfo u, good g, favorite fv, reply r, follow fl where f.categoryNo = c.categoryNo AND f.userId = u.userId AND (g.userId(+) = #{user.userId} AND g.feedNo(+) = f.FEEDNO) AND (fv.userId(+) = #{user.userId} AND fv.feedNo(+) = f.FEEDNO) AND (r.userId(+) = #{user.userId} AND r.feedNo(+) = f.FEEDNO) AND fl.REQID =#{user.userId} AND fl.resid = f.userid AND f.feedDel = 'F' AND rownum <= 10
and f.feedNo < #{filter.page}
order by f.feedno desc

SELECT f.feedNo, f.feedLimitContent, f.feedGoodCnt, f.feedReplyCnt, f.feedDate, f.feedUpdate, f.feedLock, f.categoryTemp, c.categoryNo, c.bigGroup, c.smallGroup, u.userId, u.userNick, u.userImgUuid, u.userImgUploadPath, u.userImgFileName, g.userId AS isGood, fv.userId AS isFavorite, r.userId AS isReply from feed f, category c, userInfo u, good g, favorite fv, reply r where f.FEEDDATE > sysdate - 7 AND f.categoryNo = c.categoryNo AND f.userId = u.userId AND (g.userId(+) = #{user.userId} AND g.feedNo(+) = f.FEEDNO) AND (fv.userId(+) = #{user.userId} AND fv.feedNo(+) = f.FEEDNO) AND (r.userId(+) = #{user.userId} AND r.feedNo(+) = f.FEEDNO) AND f.feedDel = 'F' AND rownum <= 10
#{category}
AND f.feedNo < #{filter.page}
ORDER BY feedgoodcnt DESC, feedreplycnt DESC

SELECT f.feedNo, f.feedLimitContent, f.feedGoodCnt, f.feedReplyCnt, f.feedDate, f.feedUpdate, f.feedLock, f.categoryTemp, c.categoryNo, c.bigGroup, c.smallGroup, u.userId, u.userNick, u.userImgUuid, u.userImgUploadPath, u.userImgFileName, g.userId AS isGood, fv.userId AS isFavorite, r.userId AS isReply from feed f, category c, userInfo u, good g, favorite fv, reply r where f.FEEDDATE > sysdate - 7 AND f.categoryNo = c.categoryNo AND f.userId = u.userId AND (g.userId(+) = #{user.userId} AND g.feedNo(+) = f.FEEDNO) AND (fv.userId(+) = #{user.userId} AND fv.feedNo(+) = f.FEEDNO) AND (r.userId(+) = #{user.userId} AND r.feedNo(+) = f.FEEDNO) AND f.feedDel = 'F' AND rownum <= 10
#{category}
AND f.feedNo < #{filter.page}
ORDER BY feeddate DESC
